	@PostMapping("/produto")
	public ResponseEntity<Produto> createProduto(@RequestBody Produto produto){
		
	
		Produto salvaProduto = produtoRepository.save(produto);
				
		return  new ResponseEntity<>( salvaProduto, HttpStatus.CREATED);
}
	@DeleteMapping("/deleta/{id}")
 
  public String deletaUsuario(@PathVariable long id) {
		
		produtoRepository.deleteById(id);
		
		return "";
	}
  
	@PutMapping("/produttos/{id}")

  public ResponseEntity<Produto> MudaProdutos(@PathVariable long id,@RequestBody Produto produto ){
		
	Optional<Produto> produtoMudado = produtoRepository.findById(id);
			
	if(produtoMudado.isPresent()) {
     
	Produto mudadoProduto = produtoMudado.get();
		

		mudadoProduto.setNome(mudadoProduto.getNome());
		mudadoProduto.setPreço(mudadoProduto.getPreço());
		
		Produto produtoJamudado = produtoRepository.save(produto);
		
		return ResponseEntity.ok(produtoJamudado);
		
	}else { return ResponseEntity.notFound().build();}
		
		
		
		
		
		
	}









